body {
  font-family: 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
  font-size: 13px;
}
div.app-layout {
  display: flex;
}
div.app-layout div.app-layout-left {
  flex: 0 0 800px;
}
div.app-layout div.app-layout-right {
  flex: 1 0 1px;
}
button {
  font-weight: bold;
  background-color: #444;
  color: #fff;
  border: 2px black solid;
  height: 25px;
  line-height: 15px;
}
button.active {
  background-color: #e9931d;
  color: #000;
}
input[type=range]::-webkit-slider-runnable-track {
  -webkit-appearance: none;
  width: 300px;
  height: 6px;
  border-radius: 3px;
  background-color: #e9931d;
}
input[type=range]::-webkit-slider-thumb {
  -webkit-appearance: none;
  border: 1px #000 solid;
  height: 15px;
  width: 5px;
  background: #444;
}
div.main-panel {
  background: #ddd;
}
table.sequencer {
  border-collapse: collapse;
}
table.sequencer td {
  border: 1px #ccc solid;
  width: 50px;
  text-align: center;
}
table.sequencer td.selected {
  background: #e9931d;
}
table.sequencer td.time {
  background: #ccc;
  text-align: right;
}
table.sequencer td.playing {
  background: #ddd;
}
div.keyboard {
  height: 200px;
  width: 100%;
  display: flex;
  position: relative;
}
div.keyboard div.clav {
  position: relative;
  flex: 1 0 auto;
  width: 4.67%;
  margin: 0;
  padding: 0;
  background-color: #fff;
  height: 99%;
  border-bottom-left-radius: 10px;
  border-bottom-right-radius: 10px;
  border: 1px #000 solid;
  border-right: none;
}
div.keyboard div.clav.pressed {
  background-color: #e9931d;
}
div.keyboard div.clav.black {
  height: 60%;
  width: 3%;
  margin-left: -5%;
  left: 2.5%;
  z-index: 100;
  background-color: #444;
  color: #fff;
}
div.keyboard div.clav.black.pressed {
  background-color: #e9931d;
}
div.keyboard div.clav span.char {
  display: block;
  position: absolute;
  bottom: 10px;
  width: 100%;
  text-align: center;
}
div.synth-panel {
  display: flex;
  padding: 15px;
  position: relative;
}
div.synth-panel span.label {
  display: inline-block;
  width: 80px;
}
div.synth-panel .col-1 {
  width: 1px;
  flex: 1 0 auto;
}
div.synth-panel .col-2 {
  width: 1px;
  flex: 1 0 auto;
}
div.synth-panel input[type=range] {
  -webkit-appearance: none;
  width: 200px;
}
div.synth-panel input[type=range]::-webkit-slide-runnable-track {
  -webkit-appearance: none;
  height: 6px;
  border-radius: 3px;
  background-color: #e9931d;
}
div.synth-panel input[type=range]::-webkit-slider-thumb {
  -webkit-appearance: none;
  border: 1px #000 solid;
  height: 15px;
  width: 8px;
  border-radius: 2px;
  margin-top: -4px;
  background: #444;
}
div.voice-selector {
  display: block;
  padding: 15px;
  text-align: right;
  border-bottom: 1px #555 solid;
}
div.voice-selector button {
  margin-left: 10px;
}
